import styled from 'styled-components'
import { palette } from 'styled-theme'
import BoxComponent from '@iso/components/utility/box'
import WithDirection from '@iso/lib/helpers/rtl'

const BoxWrapper = styled(BoxComponent)`
  .isoInvoiceTableBtn {
    display: flex;
    margin-bottom: 20px;
    a {
      margin-left: auto;
    }
  }
`

const StatusTag = styled.span`
  padding: 0 5px;
  height: 20px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: 2px;
  background-color: ${palette('primary', 0)};
  font-size: 12px;
  color: #ffffff;
  text-transform: capitalize;

  &.pending {
    background-color: ${palette('error', 0)};
  }

  &.shipped {
    background-color: ${palette('warning', 0)};
  }

  &.delivered {
    background-color: ${palette('success', 0)};
  }
`

const CardWrapper = styled.div`
  width: auto;
  overflow: inherit;
  position: relative;

  .isoInvoiceBtnView {
    display: flex;
    flex-direction: row;
    > a {
      margin: ${(props) =>
        props['data-rtl'] === 'rtl' ? '0 0 0 15px' : '0 15px 0 0'};
    }
  }

  .invoiceDltBtn {
    font-size: 18px;
    border: 0;
    color: ${palette('error', 0)};

    &:hover {
      border: 0;
      color: ${palette('error', 2)};
    }
  }
`

const Box = WithDirection(BoxWrapper)
export { Box, StatusTag }
export default WithDirection(CardWrapper)
